function Banner(){this.bannerGroup=$("#banner-group"),this.index=0,this.leftArrow=$(".left-arrow"),this.rightArrow=$(".right-arrow"),this.listenBannerHover()}Banner.prototype.toggleArrow=function(n){var r=this;n?(r.leftArrow.show(),r.rightArrow.show()):(r.leftArrow.hide(),r.rightArrow.hide())},Banner.prototype.listenBannerHover=function(){var n=this;this.bannerGroup.hover(function(){clearInterval(n.timer),n.toggleArrow(!0)},function(){n.loop(),n.toggleArrow(!1)})},Banner.prototype.loop=function(){var n=this,r=$("#banner-ul");this.timer=setInterval(function(){3<=n.index?n.index=0:n.index+=1,r.animate({left:-798*n.index},500)},2e3)},Banner.prototype.run=function(){console.log("running"),this.loop()},$(function(){(new Banner).run()});